import * as React from 'react';
import './Profile.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import './Profile.css';
import { id } from 'date-fns/locale';
import { Filter } from '@mui/icons-material';


const url = 'http://localhost:8080/api/persons/';

const Profile = () =>{
    const [dataProfile, setDataProfile] = useState({
        firstName: "",
        lastName: "",
        userName: "",
        password: "",
        email: "",
        address: "",
        phone: 0
    });

    const getData = async() => {
        const token = JSON.parse(sessionStorage.getItem("token"));
        await axios.get(url + token.token._id)
        .then(response => {
            setDataProfile({
                firstName: response.data.firstName,
                lastName: response.data.lastName,
                userName: response.data.userName,
                password: response.data.password,
                email: response.data.email,
                address: response.data.address,
                phone: response.data.phone
            });
        })
        .catch(error => {
            console.log(error.data);
        })
        console.log(dataProfile)
    }

    const updateData = async() => {
        const token = JSON.parse(sessionStorage.getItem("token"));
        console.log(url + token.token._id);
        await axios.put(url + token.token._id, {
            firstName: "Antonio",
            lastName: "Sanchez",
            userName: "dataProfile.userName",
            password: "dataProfile.password",
            email: "dataProfile.email",
            address: "dataProfile.address",
            phone: 2432
        })
        .then(response => {
            console.log("actualizado")
            console.log(response.data)
        })
        .catch(error => {
            console.log(error)
        })

        sessionStorage.clear();
        window.location.reload(true);
    }
    
    const deleteData = async() => {
        const token = JSON.parse(sessionStorage.getItem("token"));
        axios.delete(url + token.token._id)
        .then(response => console.log(response.data))
        .catch(error => console.log(error.response.data))

        sessionStorage.clear();
        window.location.reload(true);
    }

    useEffect(() => {
        getData();
    }, [])

    return(
        <Box 
        component="form"
        
        sx={{
          marginTop: 1,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          border: '2px solid #1c5d99',
          width: 600,
          height: 450,
          padding:8,
          borderRadius:1,
          boxShadow: 24,
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          
        }}
        
      >
       <Typography color='success' component="h1" variant="h4">
           Update User
       </Typography>
        <div>
          <TextField 
            label="First Name"
          />
          <TextField 
            label="Last Name"
          />
        </div>
        <div>
        <TextField fullWidth sx={{mt:2,mb:2}}
        label="Email"
        />
        <TextField fullWidth 
        label="Address"
        />
        </div>
        <div>
          <TextField  sx={{mt:2,mb:2}}
            label="UserName"
          />
            <TextField  sx={{mt:2,mb:2}}
            type="password"
            label="Password"
          />
        </div>

        <div>
        <TextField 
            type="number"
            label="Phone"
          />
        </div>
          <ButtonGroup variant="contained" fullWidth sx={{mt:2,mb:2}}>
              <Button color='success'>Save</Button>
              <Button color='error'>Cancel</Button>
          </ButtonGroup>
      </Box>
    )
}

export default Profile;
