import * as React from 'react';
import './Profile.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import './Profile.css';


const Url = 'http://localhost:8080/api/persons/';

const Profile = () =>{
    const [dataProfile, setDataProfile] = useState({
        firstName: "",
        lastName: "",
        userName: "",
        password: "",
        email: "",
        address: "",
        phone: 0
    });

    const getData = async() => {
        const token = JSON.parse(sessionStorage.getItem("token"));
        await axios.get(getPersonByIdUrl + token.token._id)
        .then(response => {
            setDataProfile({
                firstName: response.data.firstName,
                lastName: response.data.lastName,
                userName: response.data.userName,
                password: response.data.password,
                email: response.data.email,
                address: response.data.address,
                phone: response.data.phone
            });
        })
        .catch(error => {
            console.log(error.data);
        })
        console.log(dataProfile)
    }

    const updateData = async() => {
        await axios.put()
    }

    useEffect(() => {
        getData();
    }, [])

    return(
        
        <div>
            <h1 className='title'>My Profile</h1>
            <form className='profileForm'>
                <div className='input'>
                    <label>FistName</label>
                    <input type="text" value={dataProfile.firstName} 
                    onChange={e => setDataProfile({ fistName: e.target.value })}>
                    </input>
                </div>
                <div className='input'>
                    <label>LastName</label>
                    <input type="text" value={dataProfile.lastName} 
                    onChange={e => setDataProfile({ lastName: e.target.value })}>
                    </input>
                </div>
                <div className='input'>
                    <label>User</label>
                    <input type="text" value={dataProfile.userName} 
                    onChange={e => setDataProfile({ userName: e.target.value })}>
                    </input>
                </div>
                <div className='input'>
                    <label>Password</label>
                    <input type="text" value={dataProfile.password} 
                    onChange={e => setDataProfile({ password: e.target.value })}>
                    </input>
                </div>
                <div className='input'>
                    <label>Email</label>
                    <input type="text" value={dataProfile.email} 
                    onChange={e => setDataProfile({ email: e.target.value })}>
                    </input>
                </div>
                <div className='input'>
                    <label>Address</label>
                    <input type="text" value={dataProfile.address} 
                    onChange={e => setDataProfile({ address: e.target.value })}>
                    </input>
                </div>
                <div className='input'>
                    <label>Phone</label>
                    <input type="text" value={dataProfile.phone} 
                    onChange={e => setDataProfile({ phone: e.target.value })}>
                    </input>
                </div>
                <div className='buttonCenter' >
                <ButtonGroup variant="contained" sx={{ margin: 2, color: 'white'}}>
                <Button className="delete" color='error'>Delete</Button>
                <Button color='primary' onClick={updateData}>Update</Button>
                </ButtonGroup>
                </div>
            </form>
        </div>
    )
}

export default Profile;
