import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
// import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import {useState} from 'react';
import axios from 'axios';
import Modal from '@mui/material/Modal';
import { Link } from 'react-router-dom';
 
const loginUrl = "http://localhost:8080/api/persons/login";
const loginValidationUrl = "http://localhost:8080/api/persons/login/validate";

const Login = () =>{
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useState('');
    const [msgError, setMsgError] = useState('');


    const handleOnSubmit = async(event) =>{
      event.preventDefault();
      await axios.post(loginUrl, 
        {
          userName: userName,
          password: password
        })
      .then(response => {
        console.log(response.data);
        validationToken(response.data.token);
      })
      .catch(error => {
        console.log(error.response);
        setMsgError(error.response.data);
      })
    }

    const validationToken = async(token) => {
      await axios.post(loginValidationUrl, {
        headers: {
          token: token
        }
      })
      .then(response => {
        localStorage.setItem("token", token);
        console.log("local storage " + localStorage.getItem("token"));
        console.log(response);
      })
      .catch(error => console.log(error));
    }

    const style = {
        position: 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        width: 400,
        bgcolor: '#e6ffff',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
        alignItems: 'center',
        borderRadius: 1,
      };
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    return (
        
          <Container component="main" maxWidth="xs">
            <CssBaseline />
            <Box
              sx={{
                marginTop: 8,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
              }}
            >
              <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
                <LockOutlinedIcon />
              </Avatar>
              <Typography component="h1" variant="h5">
                Login
              </Typography>
              <Box component="form"  >
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  id="username"
                  label="Username"
                  name="username"
                  autoComplete="username"
                  autoFocus
                  value={userName}
                  onChange={e => setUserName(e.target.value)}
                />
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="current-password"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                />
                <FormControlLabel
                  control={<Checkbox value="remember" color="primary" />}
                  label="Remember me"
                />
                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  sx={{ mt: 3, mb: 2 }}
                  onClick={handleOnSubmit}
                >
                  Sign In
                </Button>
                <Link to='/SignUp'>Sign Up</Link>
              </Box>
            </Box>
            
          </Container>
        
      );
}
export default Login;
