import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import './Profile.css';
import axios from 'axios';
import { useState, useEffect } from 'react';


const getPersonByIdUrl = 'http://localhost:8080/api/persons/';

const Profile = () =>{
    const [dataProfile, setDataProfile] = useState({
        firstName: "",
        lastName: "",
        userName: "",
        password: "",
        email: "",
        address: "",
        phone: 0
    });

    const getData = async() => {
        const token = JSON.parse(sessionStorage.getItem("token"));

        await axios.get(getPersonByIdUrl + token.token._id)
        .then(response => {
            setDataProfile({
                firstName: response.data.firstName,
                lastName: response.data.lastName,
                userName: response.data.userName,
                password: response.data.password,
                email: response.data.email,
                address: response.data.address,
                phone: response.data.phone
            });
        })
        .catch(error => {
            console.log(error.data);
        })
    }

    useEffect(() => {
        getData();
    }, [])

    return(
        
        <div>
            <Box
            component="form"
            sx={{
                '& .MuiTextField-root': { m: 2, width: '25ch' },
                }}
            noValidate
            autoComplete="off"
            ></Box>
            <h1>My Profile</h1>
            <form className='profileForm'>
            <TextField
            required
            id="filled-required"
            label="FirstName"
            value={dataProfile.firstName}
            variant="filled"
            onChange={e => setDataProfile({ firstName: e.target.value })}
            />
            <TextField
            required
            id="filled-required"
            label="LastName"
            value={dataProfile.lastName}
            variant="filled"
            />
            <TextField
            required
            id="filled-required"
            label="UserName"
            value={dataProfile.userName}
            variant="filled"
            />
            <TextField
            required
            type="password"
            id="filled-required"
            label="Password"
            value={dataProfile.password}
            variant="filled"
            />
            <TextField
            required
            type="email"
            id="filled-required"
            label="Email"
            value={dataProfile.email}
            variant="filled"
            />
            <TextField
            required
            id="filled-required"
            label="Address"
            value={dataProfile.address}
            variant="filled"
            />
            </form>
            
        </div>
    )
}

export default Profile;
