import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import './Profile.css';
import axios from 'axios';
import { useState, useEffect } from 'react';


const getPersonByIdUrl = 'http://localhost:8080/api/persons/';

const Profile = () =>{
    const [dataProfile, setDataProfile] = useState({
        firstName: "SoyUnaPrueba",
        lastName: "Pruebita",
        userName: "prueba123",
        password: "prueba123",
        email: "prueba@gmail.com",
        address: "prueba 200",
        phone: 1234032
    });

    const getData = async() => {
        const token = JSON.parse(sessionStorage.getItem("token"));

        await axios.get(getPersonByIdUrl + token.token._id)
        .then(response => {
            setDataProfile({
                ...dataProfile,
                firstName: response.data.firstName,
                lastName: response.data.lastName,
                userName: response.data.userName,
                password: "prueba123",
                email: "prueba@gmail.com",
                address: "prueba 200",
                phone: 1234032
            });
        })
        .catch(error => {
            console.log(error.data);
        })

        console.log(dataProfile);
    }

    useEffect(() => {
        getData();
    },[])

    return(
        
        <div>
            <Box
            component="form"
            sx={{
                '& .MuiTextField-root': { m: 1, width: '25ch' },
                }}
            noValidate
            autoComplete="off"
            ></Box>
            <h1>Profile</h1>
            <form className='profileForm'>
            <TextField
            required
            id="filled-required"
            label="FirstName"
            defaultValue={dataProfile.firstName}
            variant="filled"
            />
            <TextField
            required
            id="filled-required"
            label="LastName"
            defaultValue={dataProfile.lastName}
            variant="filled"
            />
            <TextField
            required
            id="filled-required"
            label="UserName"
            defaultValue={dataProfile.userName}
            variant="filled"
            />
            <TextField
            required
            type="password"
            id="filled-required"
            label="Password"
            defaultValue={dataProfile.password}
            variant="filled"
            />
            <TextField
            required
            type="email"
            id="filled-required"
            label="Email"
            defaultValue={dataProfile.email}
            variant="filled"
            />
            <TextField
            required
            id="filled-required"
            label="Address"
            defaultValue={dataProfile.address}
            variant="filled"
            />
            </form>
            
        </div>
    )
}

export default Profile;
